	function Rich_Web_VSlider_Del_Callback()
	{
		$number = sanitize_text_field($_POST['foobar']);
		global $wpdb;
		$table_name2 = $wpdb->prefix . "rich_web_video_slider_manager";
		$table_name3 = $wpdb->prefix . "rich_web_video_slider_videos";
