				$wpdb->query($wpdb->prepare("DELETE FROM $RW_VS_Load_Q_Table WHERE RW_VS_ID=%s", $RW_VS_ID));
				echo "delete";
				die();
			}else{
				echo "false";
				die();
			}
		}else{
			echo "none";
				die();
		}
	}
